import React, { Component } from 'react';
import { Loop, Stage, Sprite } from 'react-game-kit';

class SimpleGame extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playerX: 100,
      playerY: 100,
    };
  }

  componentDidMount() {
    window.addEventListener('keydown', this.handleKeyPress);
  }

  componentWillUnmount() {
    window.removeEventListener('keydown', this.handleKeyPress);
  }

  handleKeyPress = (e) => {
    const { playerX, playerY } = this.state;
    const speed = 5;

    switch (e.key) {
      case 'ArrowLeft':
        this.setState({ playerX: playerX - speed });
        break;
      case 'ArrowRight':
        this.setState({ playerX: playerX + speed });
        break;
      case 'ArrowUp':
        this.setState({ playerY: playerY - speed });
        break;
      case 'ArrowDown':
        this.setState({ playerY: playerY + speed });
        break;
      default:
        break;
    }
  };

  render() {
    const { playerX, playerY } = this.state;

    return (
      <Loop>
        <Stage width={800} height={600}>
          <Sprite
            x={playerX}
            y={playerY}
            width={50}
            height={50}
            image="../mario.png"
          />
        </Stage>
      </Loop>
    );
  }
}

export default SimpleGame;
